mapper.select:
select id
from board_type
where name = #{boardTypeName};

select id
from category
where name = #{categoryName};

SELECT *
FROM board
WHERE board.id = #{boardId}

mapper.update:
update board
set state = 1
where id = #{boardId}

mapper.delete:
delete
from board_like
where board_id = #{boardId}
  and user_id = #{userId}

