select.if:
SELECT b.id AS boardId, title, SUBSTRING(b.body, 1, 30) as body, b.created_at AS createdAt, b.modified_at AS modifiedAt, IF(is_anonymous, '익명',u.nickname) AS userNickname, comment_cnt AS commentCnt, like_cnt AS likeCnt, is_anonymous AS isAnonymous, is_hide AS isHide, IF(blike.user_id IS NOT NULL, 1, 0) as isLike, b.images_url as imagesUrlString, GROUP_CONCAT(t.name ORDER BY t.name ASC SEPARATOR ',') as tagStrings, type_id AS typeId, bt.name AS type FROM board b INNER JOIN user u ON b.user_id = u.id INNER JOIN board_type bt ON b.type_id = bt.id LEFT OUTER JOIN board_like blike ON b.id = blike.board_id AND blike.user_id = #{userId} LEFT OUTER JOIN board_tag ON b.id = board_tag.board_id LEFT OUTER JOIN tag t ON board_tag.tag_id = t.id WHERE b.category_id = #{categoryId} AND b.state = 0 AND is_hide = 0
AND (lower(title) like lower(CONCAT('%', #{boardSearchDto.content}, '%')) or lower(body) like lower(CONCAT('%', #{boardSearchDto.content}, '%')))
AND b.type_id = #{boardSearchDto.boardTypeId}
GROUP BY b.id LIMIT #{pagingDto.recordSize} OFFSET #{pagingDto.offset}

SELECT b.id AS boardId, title, SUBSTRING(b.body, 1, 30) as body, b.created_at AS createdAt, b.modified_at AS modifiedAt, IF(is_anonymous, '익명',u.nickname) AS userNickname, comment_cnt AS commentCnt, like_cnt AS likeCnt, is_anonymous AS isAnonymous, is_hide AS isHide, IF(blike.user_id IS NOT NULL, 1, 0) as isLike, b.images_url as imagesUrlString, GROUP_CONCAT(t.name ORDER BY t.name ASC SEPARATOR ',') as tagStrings, type_id AS typeId, board_type.name AS type FROM tag t INNER JOIN board_tag bt ON t.id = bt.tag_id AND t.name LIKE CONCAT(#{boardSearchDto.tagName}, '%') INNER JOIN board b ON bt.board_id = b.id INNER JOIN user u ON b.user_id = u.id LEFT OUTER JOIN board_like blike ON b.id = blike.board_id AND blike.user_id = #{userId} INNER JOIN board_type ON b.type_id = board_type.id WHERE b.category_id = #{categoryId} AND b.state = 0 AND is_hide = 0
AND (lower(title) like lower(CONCAT('%', #{boardSearchDto.content}, '%')) or lower(body) like lower(CONCAT('%', #{boardSearchDto.content}, '%')))
AND b.type_id = #{boardSearchDto.boardTypeId}
GROUP BY b.id LIMIT #{pagingDto.recordSize} OFFSET #{pagingDto.offset}

SELECT COUNT(*) FROM board b WHERE b.category_id = #{categoryId} AND b.state = 0 AND is_hide = 0
AND (lower(title) like lower(CONCAT('%', #{boardSearchDto.content}, '%')) or lower(body) like lower(CONCAT('%', #{boardSearchDto.content}, '%')))
AND b.type_id = #{boardSearchDto.boardTypeId}

SELECT COUNT(distinct b.id) FROM tag t INNER JOIN board_tag bt ON t.id = bt.tag_id AND t.name LIKE CONCAT(#{boardSearchDto.tagName}, '%') INNER JOIN board b ON bt.board_id = b.id WHERE b.category_id = #{categoryId} AND b.state = 0 AND is_hide = 0
AND (lower(title) like lower(CONCAT('%', #{boardSearchDto.content}, '%')) or lower(body) like lower(CONCAT('%', #{boardSearchDto.content}, '%')))
AND b.type_id = #{boardSearchDto.boardTypeId}

mapper.select:
select b.id AS boardId, b.title, SUBSTRING(b.body, 1, 30) as body, b.created_at AS createdAt, b.modified_at AS modifiedAt, IF(is_anonymous, '익명',u.nickname) AS userNickname, comment_cnt AS commentCnt, like_cnt AS likeCnt, is_anonymous AS isAnonymous, is_hide AS isHide, IF(blike.user_id IS NOT NULL, 1, 0) as isLike, b.images_url as imagesUrlString, GROUP_CONCAT(t.name ORDER BY t.name ASC SEPARATOR ',') as tagStrings FROM board b INNER JOIN user u ON b.user_id = u.id INNER JOIN poll p ON b.id = p.board_id LEFT OUTER JOIN board_like blike ON b.id = blike.board_id AND blike.user_id = #{userId} LEFT OUTER JOIN board_tag ON b.id = board_tag.board_id LEFT OUTER JOIN tag t ON board_tag.tag_id = t.id WHERE is_hide = 0 AND b.state = 0 AND b.category_id = #{categoryId}
AND b.type_id = #{boardTypeId}
GROUP BY b.id ORDER BY b.id DESC LIMIT #{pagingDto.recordSize} OFFSET #{pagingDto.offset}
