mapper.delete:
UPDATE comment
SET state = 1
WHERE id = #{commentId}

mapper.select:
SELECT COUNT(*)
FROM comment_like
WHERE comment_like.comment_id = #{commentId}

mapper.delete:
DELETE
FROM comment_like
WHERE comment_id = #{commentId}
  AND user_id = #{userId}

mapper.select:
SELECT user_id, report_id
FROM comment_report
WHERE comment_report.comment_id = #{commentId}

SELECT COUNT(*)
FROM comment
WHERE board_id = #{boardId}

mapper.update:
UPDATE comment
SET state = #{state}
WHERE id = #{commentId}

mapper.select:
SELECT user_id
FROM board
WHERE id = #{boardId}
